mapper.select:
SELECT * FROM ( SELECT BOOK.id AS id, BOOK.employeeId AS employeeId, SCHEDULE.startDate AS startDate, SCHEDULE.endDate AS endDate, SCHEDULE.regDate AS regDate, meetingRoomId AS targetId, 'MEETING_ROOM' AS targetType, MEETING_ROOM.NAME AS targetName FROM BOOK JOIN MEETING_ROOM ON BOOK.meetingRoomId = MEETING_ROOM.ID JOIN SCHEDULE ON SCHEDULE.bookId = BOOK.id ON BOOK.meetingRoomId = MEETING_ROOM.id
UNION ALL
SELECT BOOK.id AS id, BOOK.employeeId AS employeeId, SCHEDULE.startDate AS startDate, SCHEDULE.endDate AS endDate, SCHEDULE.regDate AS regDate, furnitureId AS targetId, 'FURNITURE' AS targetType, FURNITURE.NAME AS targetName FROM BOOK JOIN FURNITURE ON BOOK.furnitureId = FURNITURE.ID JOIN SCHEDULE ON SCHEDULE.bookId = BOOK.id ON BOOK.meetingRoomId = MEETING_ROOM.id) A WHERE A.employeeId = #{employeeId}
