mapper.select:
SELECT a.* , e.*, j.name AS jobName FROM approval a
    join employee e on a.employeeid = e.id
    JOIN job j ON e.jobid = j.id WHERE a.id = #{id}

mapper.update:
UPDATE approval
SET state = #{state}
WHERE id = #{id}

mapper.select:
SELECT *
FROM APPROVAL_FORM
WHERE id = #{id, jdbcType=INTEGER}
