mapper.insert:
INSERT INTO SOLDERING(id, employeeId, state) SELECT SOLDERING_ID_SEQ.NEXTVAL, A.* FROM (
SELECT #{item.employeeId}, #{item.state} FROM DUAL
) A

mapper.select:
SELECT * FROM EMPLOYEE WHERE ID NOT IN ( SELECT employeeId FROM COMMUTE WHERE STATE = 'WORK_ON' AND TRUNC(regDate) = TRUNC(SYSDATE)) AND ID NOT IN ( SELECT employeeId FROM VACATION WHERE startDate + NUMTODSINTERVAL(vacationDate, 'DAY') > SYSDATE
AND vacationDate > 1
)
