mapper.select:
SELECT COUNT(*)
FROM PROJECT_WORK
WHERE projectId = #{selectedId}

SELECT COUNT(*)
FROM project_work
WHERE projectId = #{selectedId}
AND state = #{state}

SELECT COUNT(*)
FROM project_work
WHERE projectId = #{selectedId}
AND type = #{type}

SELECT *
FROM PROJECT_USER
WHERE
PROJECTID =#{projectId}
AND
EMPLOYEEID =#{employeeId}
