mapper.select:
SELECT pw.title, e.name AS employeeName, pw.priority, pw.state FROM project_work pw JOIN project_user pu ON pw.projectUserId = pu.id JOIN employee e ON pu.employeeId = e.id WHERE pw.projectId = #{projectId}
AND pw.title LIKE '%' || #{keyword} || '%'
AND e.name LIKE '%' || #{keyword} || '%'

SELECT COUNT(*)
FROM project_work
WHERE projectId = #{projectId}

SELECT COUNT(*)
FROM PROJECT_WORK
WHERE projectId = #{selectedId}

SELECT COUNT(*)
FROM project_work
WHERE projectId = #{selectedId}
AND state = #{state}

SELECT COUNT(*)
FROM project_work
WHERE projectId = #{selectedId}
AND type = #{type}

SELECT *
FROM PROJECT_USER
WHERE
PROJECTID =#{projectId}
AND
EMPLOYEEID =#{employeeId}
